import React from "react";
import articleData from "./articledata";

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      match: false
    };
    this.search = this.search.bind(this);
  }
  search() {
    let input = document.getElementById("searchbar").value;
    input = input.toLowerCase().trim();

    // ignore empty strings, spaces, and strings less than 3 char in length
    if (input === " " || input === "" || input.length < 3) {
      this.setState({ match: false }, function() {
        console.log("The searchbar is empty and match = " + this.state.match);
      });
    } else {
      this.setState({ match: true }, function() {
        console.log(input, "match = " + this.state.match);
      });
    }
  }
  render() {
    // initialize matches as false
    let matches = false;

    // get the searchbar input value
    let input = document.getElementById("searchbar").value;
    input = input.toLowerCase().trim();

    // if the searchbar is empty
    if (input === " " || input === "" || input.length < 3) {
      matches = (
        <div id="failed-search-page">
          <h1>Search Page</h1>
        </div>
      );
    } else {
      const articleMatches = [];
      // if the searchbar isn't empty
      for (let i = 0; i < articleData.length; i++) {
        if (articleData[i].title.toLowerCase().includes(input)) {
          articleMatches.push(articleData[i]);
        }
      }
      if (articleMatches.length === 0) {
        // if no matches were found
        matches = (
          <div id="failed-search-page">
            <h1>=(</h1>
            <h1>Sorry, we couldn't find a match for: {input}</h1>
          </div>
        );
      } else {
        matches = articleMatches;
        console.log(articleMatches);
        {
          articleData.map(item => {
            if (item.title === "How To Create A Retention Policy") {
              return (
                <FourStepArticle
                  category={item.category}
                  title={item.title}
                  subtitleOne={item.subtitleOne}
                  descriptionOne={item.descriptionOne}
                  instructionsOne={item.instructionsOne.map(item => (
                    <li>{item}</li>
                  ))}
                  subtitleTwo={item.subtitleTwo}
                  descriptionTwo={item.descriptionTwo}
                  instructionsTwo={item.instructionsTwo.map(item => (
                    <li>{item}</li>
                  ))}
                  subtitleThree={item.subtitleThree}
                  descriptionThree={item.descriptionThree}
                  instructionsThree={item.instructionsThree.map(item => (
                    <li>{item}</li>
                  ))}
                  subtitleFour={item.subtitleFour}
                  descriptionFour={item.descriptionFour}
                  instructionsFour={item.instructionsFour.map(item => (
                    <li>{item}</li>
                  ))}
                  img1={item.img1}
                  alt1={item.alt1}
                  img2={item.img2}
                  alt2={item.alt2}
                  img3={item.img3}
                  alt3={item.alt3}
                />
              );
            } else if (item.title === "I need access to a network drive") {
              return (
                <TwoStepArticleWithNotes
                  category={item.category}
                  title={item.title}
                  subtitleOne={item.subtitleOne}
                  descriptionOne={item.descriptionOne}
                  instructionsOne={item.instructionsOne.map(item => (
                    <li>{item}</li>
                  ))}
                  subtitleTwo={item.subtitleTwo}
                  descriptionTwo={item.descriptionTwo}
                  instructionsTwo={item.instructionsTwo.map(item => (
                    <li>{item}</li>
                  ))}
                  notes={item.notes.map(item => (
                    <p class="article-notes">{item}</p>
                  ))}
                  img={item.img}
                  alt={item.alt}
                />
              );
            } else if (item.title === "How to add a Printer by IP Address") {
              return (
                <TwoStepArticle
                  category={item.category}
                  title={item.title}
                  subtitleOne={item.subtitleOne}
                  descriptionOne={item.descriptionOne}
                  instructionsOne={item.instructionsOne.map(item => (
                    <li>{item}</li>
                  ))}
                  subtitleTwo={item.subtitleTwo}
                  descriptionTwo={item.descriptionTwo}
                  instructionsTwo={item.instructionsTwo.map(item => (
                    <li>{item}</li>
                  ))}
                  img={item.img}
                  alt={item.alt}
                />
              );
            } else if (item.title === "How To Customize Mailbox Size") {
              return (
                <ArticleWithNotes
                  category={item.category}
                  title={item.title}
                  subtitle={item.subtitle}
                  description={item.description}
                  instructions={item.instructions.map(item => (
                    <li>{item}</li>
                  ))}
                  notes={item.notes.map(item => (
                    <p class="article-notes">{item}</p>
                  ))}
                  img={item.img}
                  alt={item.alt}
                />
              );
            } else {
              return (
                <Article
                  category={item.category}
                  title={item.title}
                  subtitle={item.subtitle}
                  description={item.description}
                  instructions={item.instructions.map(item => (
                    <li>{item}</li>
                  ))}
                  img={item.img}
                  alt={item.alt}
                />
              );
            }
          });
        }
      }
    }

    return (
      <div>
        <h1>Moogley Search Page</h1>
        {matches}
      </div>
    );
  }
}

export default Search;
