import React from "react";
import articleData from "./articledata";

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      match: false
    };
    this.search = this.search.bind(this);
  }
  search() {
    let input = document.getElementById("searchbar").value;
    input = input.toLowerCase().trim();

    // ignore empty strings, spaces, and strings less than 3 char in length
    if (input === " " || input === "" || input.length < 3) {
      this.setState({ match: false }, function() {
        console.log("The searchbar is empty and match = " + this.state.match);
      });
    } else {
      this.setState({ match: true }, function() {
        console.log(input, "match = " + this.state.match);
      });
    }
  }
  render() {

   // initialize matches as false
   let matches = false;

   // get the searchbar input value
   let input = document.getElementById("searchbar").value;
   input = input.toLowerCase().trim();

   // if the searchbar is empty
   if (input === " " || input === "" || input.length < 3) {
    matches = (
     <div id="failed-search-page">
          <h1>Search Page</h1>
        </div>
      );
     } else {
      const articleMatches = [];
      // if the searchbar isn't empty
      for (let i = 0; i < articleData.length; i++) {
        if (articleData[i].title.toLowerCase().includes(input)) {
          articleMatches.push(articleData[i]);
        }
      }
      if (articleMatches.length === 0) {
        // if no matches were found
        matches = (
          <div id="failed-search-page">
            <h1>=(</h1>
            <h1>Sorry, we couldn't find a match for: {input}</h1>
          </div>
        );
      } else {
        matches = articleMatches;
      }
    }


    return (
      <div>
        <h1>Moogley Search Page</h1>
        {matches}
      </div>
    );
  }
}

export default Search;
